import { useState, useEffect } from 'react';
import TeamAPI from '../api/TeamAPI'
import styled from 'styled-components';
import { Link } from "react-router-dom";
import imgHome from '../images/home_button.png'


const MemberOut = () => {
    const [inputId, setInputId] = useState("");
    const [inputPwd, setInputPwd] = useState("");

    const MemberListBlock = styled.div`
        box-sizing: border-box;
        padding-bottom: 3em;
        width: 768px;
        margin: 0 auto;
        margin-top: 2rem;
        @media screen and (max-width: 768px) {
            witdh: 100%;
            padding-left: 1em;
            padding-right:1em;
        }
    `;

    const MemberList = styled.table`
        border-collapse: collapse;
        width: 768px;
        margin: 0 auto;
        font-size: 1.125em;
        @media screen and (max-width: 768px) {
            witdh: 100%;
        }
        th, td {
            border:1px solid #ccc;
            padding: 2px;
        }
        th {
            background-color: bisque;
        }
    `;

    const MemberTitle = styled.table`
        font-size: 2em;
        text-align: center;
    `;


const onClickPwCheck = async() => {
    try {
        const res = await TeamAPI.MemberOut(inputId);
        console.log("이거 뭐냐고!!! : " + res.data.result);
       
        if(res.data.result === "OK") {
            window.localStorage.setItem("userId", inputId);
            alert('진짜 탈퇴할거야?')
            // if (onclick) {
            // window.location.replace("/home");
            // }
            console.log("탈퇴 완..");
        }
    } catch (e) {
        console.log("탈퇴 에러..");
    }
}



    // useEffect(() => {
        
    //     const memberData = async () => {
    //         setLoading(true);
    //         try {
    //             const response = await KhApi.memberInfo();
    //             setMemberInfo(response.data);
    //             console.log(response.data)
    //         } catch (e) {
    //             console.log(e);
    //         }
    //         setLoading(false);
    //     };
    //     memberData();
    // }, []);

    // if(loading) {
    //     return <MemberListBlock>대기 중...</MemberListBlock>
    // }

    return(
        <MemberListBlock>
            <MemberList>
                <MemberTitle>회원 탈퇴 하기</MemberTitle>

                {/* <div className="item2">
                    <input className="input" placeholder="패스워드" value ={inputPw} />
                </div>
                 <button>확인</button> */}
            </MemberList>

            <Link to="/MemberOut" className="link-box">
                <img className="link-img" src={imgHome} alt="Out" />
                <p onClick={onClickPwCheck}>탈퇴하기</p>
            </Link>
            <Link to="/home" className="link-box">
                <img className="link-img" src={imgHome} alt="HOME" />
                <p>HOME으로 이동</p>
            </Link>
        </MemberListBlock>
    );
}
export default MemberOut;
