const Msg = styled.div`
  color: red;
  font-size: .8em;
  display: flex;
  justify-content: flex-end;
  align-items: center;
`;

const memberObj = {
  isId: "",
  isPassword: "",
  isName: "",
  isGender: "",
  isBirth: "",
  isRegion1: "",
  isRegion2: ""
};


function SignUp() {


